import { UserType } from "../HW8";

type ActionType =
  | { type: "sort"; payload: "up" | "down" }
  | { type: "check"; payload: number };

export const homeWorkReducer = (
  state: UserType[],
  action: ActionType
): UserType[] => {
  // need to fix any
  switch (action.type) {
    case "sort": {
      // by name
      const sortedArray = [...state];
     const a = sortedArray.sort((a, b) => a.age - b.age);
      return [...a]; // need to fix
    }
    case "check": {
      return state; // need to fix
    }
    default:
      return state;
  }
};
