import React, {
  DetailedHTMLProps,
  InputHTMLAttributes,
  HTMLAttributes,
  useState,
  KeyboardEvent,
  useEffect,
} from "react";
import s from "./SuperEditableSpan.module.css";
import SuperInputText from "../../../hw04/common/c1-SuperInputText/SuperInputText";
import editIcon from "./editIcon.svg";
import { restoreState, saveState } from "../../localStorage/localStorage";

// тип пропсов обычного инпута
type DefaultInputPropsType = DetailedHTMLProps<
  InputHTMLAttributes<HTMLInputElement>,
  HTMLInputElement
>;
// тип пропсов обычного спана
type DefaultSpanPropsType = DetailedHTMLProps<
  HTMLAttributes<HTMLSpanElement>,
  HTMLSpanElement
>;

// здесь мы говорим что у нашего инпута будут такие же пропсы как у обычного инпута, кроме type
// (чтоб не писать value: string, onChange: ...; они уже все описаны в DefaultInputPropsType)
type SuperEditableSpanType = Omit<DefaultInputPropsType, "type"> & {
  // и + ещё пропсы которых нет в стандартном инпуте
  onChangeText?: (value: string) => void;
  onEnter?: () => void;
  error?: string;
  spanProps?: DefaultSpanPropsType & { defaultText?: string }; // пропсы для спана
};

const SuperEditableSpan: React.FC<SuperEditableSpanType> = ({
  autoFocus,
  onBlur,
  onEnter,
  spanProps,
  onChangeText,
  ...restProps // все остальные пропсы попадут в объект restProps
}) => {
  const [editMode, setEditMode] = useState<boolean>(false);
  const { children, onDoubleClick, className, defaultText, ...restSpanProps } =
    spanProps || {};



    // useEffect(() => {
    //     setLocalValue(restProps.value || "");
    //   }, [restProps.value]);


    // const [localValue, setLocalValue] = useState(restProps.value || "");


  const onEnterCallback = () => {
    // выключить editMode при нажатии Enter // делают студенты

    onEnter?.();
    setEditMode(false);


  };
  const onBlurCallback = (e: React.FocusEvent<HTMLInputElement>) => {
    // выключить editMode при нажатии за пределами инпута // делают студенты

    onBlur?.(e);
    setEditMode(false);
    // restProps.onChangeText?.(restProps.value as string || "hello") ;

  };
  const onDoubleClickCallBack = (
    e: React.MouseEvent<HTMLSpanElement, MouseEvent>
  ) => {
    // включить editMode при двойном клике // делают студенты

    onDoubleClick?.(e);
    setEditMode(true);
    onChangeText?.("")
  };

  const spanClassName = s.span + (className ? " " + className : "");

  return (
    <>
      {editMode ? (
        <SuperInputText
          autoFocus={autoFocus || true}
          onBlur={onBlurCallback}
          onEnter={onEnterCallback}
          className={s.input}
          value={restProps.value}
          {...restProps} // отдаём инпуту остальные пропсы если они есть (value например там внутри)
        />
      ) : (
        <div className={s.spanBlock}>
          <img src={editIcon} className={s.pen} alt={"edit"} />
          <span
            onDoubleClick={onDoubleClickCallBack}
            className={spanClassName}
            {...restSpanProps}
          >
            {/*если нет захардкодженного текста для спана, то значение инпута*/}

            {children || restProps.value || defaultText}
            {/* { restProps.value  ? restProps.value  : defaultText} */}
            {/* { defaultText  ? defaultText : restProps.value } */}
          </span>
        </div>
      )}
    </>
  );
};

export default SuperEditableSpan;
