import { UserType } from "../HW8";

type ActionType =
  | { type: "sort"; payload: "up" | "down" }
  | { type: "check"; payload: number };

export const homeWorkReducer = (
  state: UserType[],
  action: ActionType
): UserType[] => {
  // need to fix any
  switch (action.type) {
    case "sort": {
      // by name
      const sortedArray = [...state].sort((a, b) => {
        if (action.payload === "up") {
          return a.age - b.age;
        } else {
          return b.age - a.age;
        }
      });
      return sortedArray; // need to fix
    }
    case "check": {
      const sortedArray = [...state].filter((item) => item.age > 18);
    //   console.log(sortedArray);
      return sortedArray; // need to fix
    }
    default:
      return state;
  }
};
