  };

  console.log(typeof value, "SuperRadio");
  console.log(value, "SuperRadio");

  const finalRadioClassName = s.radio + (className ? " " + className : "");
  const spanClassName =
    s.span + (spanProps?.className ? " " + spanProps.className : "");

  const mappedOptions: any[] = options
    ? options.map((o) => {
        console.log(typeof Number(o.value), "o.value");
        console.log(o.value, "o.value");
        return (
          <label key={name + "-" + o.id} className={s.label}>
            <input
              id={id + "-input-" + o.id}
              className={finalRadioClassName}
              type={"radio"}
              name="developer"
              value={value}
              // checked={true}
              checked={value === Number(o.id)}
              // name, checked, value делают студенты
              onChange={onChangeCallback}
              {...restProps}
            />
            <span
              id={id + "-span-" + o.id}
              {...spanProps}
              className={spanClassName}
            >
              {o.value}
            </span>
          </label>
        );
      })
    : [];

  return <div className={s.options}>{mappedOptions}</div>;
};

export default SuperRadio;
